import React, { useEffect, useState } from 'react';

function CarritoPage() {
  const [carrito, setCarrito] = useState(() => {
    const guardado = localStorage.getItem('carrito');
    return guardado ? JSON.parse(guardado) : [];
  });

  useEffect(() => {
    localStorage.setItem('carrito', JSON.stringify(carrito));
  }, [carrito]);

  const eliminar = (index) => {
    setCarrito(prev => prev.filter((_, i) => i !== index));
  };

  const finalizar = () => {
    if (carrito.length === 0) return alert('El carrito está vacío');
    const total = carrito.reduce((s, p) => s + p.precio, 0);
    alert(`Compra realizada. Total: Q${total.toFixed(2)}`);
    setCarrito([]);
  };

  return (
    <section className="carrito">
      <div className="container">
        <h2>Mi Carrito</h2>
        {carrito.length === 0 ? (
          <p className="carrito-vacio">No tienes productos</p>
        ) : (
          <>
            <div className="carrito-items">
              {carrito.map((p, i) => (
                <div key={`${p.id}-${i}`} className="carrito-item">
                  <div>
                    <h3>{p.nombre}</h3>
                    <p>Q{p.precio.toFixed(2)}</p>
                  </div>
                  <button onClick={() => eliminar(i)}>Eliminar</button>
                </div>
              ))}
            </div>
            <div className="carrito-total">
              <h3>
                Total: Q{carrito.reduce((s, p) => s + p.precio, 0).toFixed(2)}
              </h3>
              <button className="btn-comprar" onClick={finalizar}>Finalizar Compra</button>
            </div>
          </>
        )}
      </div>
    </section>
  );
}

export default CarritoPage;
