import React, { useEffect, useState } from 'react';
import { api } from '../../api';

function AdminPage() {
  const [productos, setProductos] = useState([]);
  const [form, setForm] = useState({
    nombre: '',
    precio: '',
    categoria: 'Hombre',
    imagen: '',
    descripcion: ''
  });

  const cargar = async () => {
    const res = await fetch('http://localhost:3001/api/productos');
    const data = await res.json();
    setProductos(data);
  };

  useEffect(() => { cargar(); }, []);

  const onChange = (e) => setForm(f => ({ ...f, [e.target.name]: e.target.value }));

  const agregar = async (e) => {
    e.preventDefault();
    const res = await fetch('http://localhost:3001/api/productos', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...form, precio: parseFloat(form.precio) })
    });
    if (res.ok) {
      setForm({ nombre: '', precio: '', categoria: 'Hombre', imagen: '', descripcion: '' });
      cargar();
    }
  };

  const eliminar = async (id) => {
    await fetch(`http://localhost:3001/api/productos/${id}`, { method: 'DELETE' });
    cargar();
  };

  return (
    <section className="admin">
      <div className="container">
        <h2>Panel de Administración</h2>

        <div className="admin-form">
          <h3>Agregar Producto</h3>
          <form onSubmit={agregar}>
            <input name="nombre" placeholder="Nombre" value={form.nombre} onChange={onChange} />
            <input name="precio" type="number" step="0.01" placeholder="Precio (Q)" value={form.precio} onChange={onChange} />
            <select name="categoria" value={form.categoria} onChange={onChange}>
              <option>Hombre</option>
              <option>Mujer</option>
              <option>Unisex</option>
            </select>
            <input name="imagen" placeholder="imagen.jpg" value={form.imagen} onChange={onChange} />
            <textarea name="descripcion" placeholder="Descripción" value={form.descripcion} onChange={onChange} />
            <button type="submit">Agregar Producto</button>
          </form>
        </div>

        <div className="admin-lista">
          <h3>Productos Existentes</h3>
          {productos.map(p => (
            <div className="admin-producto-item" key={p.id}>
              <div><strong>{p.nombre}</strong> - Q{p.precio.toFixed(2)} ({p.categoria})</div>
              <button onClick={() => eliminar(p.id)}>Eliminar</button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default AdminPage;
