import React, { useState, useEffect } from 'react';

function Tienda({ agregarAlCarrito, categoriaFija }) {
  const [productos, setProductos] = useState([]);
  const [productosFiltrados, setProductosFiltrados] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState(categoriaFija || 'Todos');
  const [precioSeleccionado, setPrecioSeleccionado] = useState('todos');

  useEffect(() => {
    cargarProductos();
  }, []);

  useEffect(() => {
    if (productos.length > 0) {
      if (categoriaFija) {
        filtrarCategoria(categoriaFija);
      } else {
        setProductosFiltrados(productos);
      }
    }
  }, [productos, categoriaFija]);

  const cargarProductos = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/productos');
      const data = await response.json();
      console.log('Productos cargados:', data); // Para debug
      setProductos(data);
      setProductosFiltrados(data);
    } catch (error) {
      console.error('Error al cargar productos:', error);
    }
  };

  const filtrarCategoria = (categoria) => {
    setCategoriaSeleccionada(categoria);
    let filtrados = productos;

    if (categoria !== 'Todos') {
      filtrados = productos.filter(p => p.categoria === categoria);
    }

    aplicarFiltroPrecio(filtrados, precioSeleccionado);
  };

  const filtrarPorPrecio = (rango) => {
    setPrecioSeleccionado(rango);
    let filtrados = productos;

    if (categoriaSeleccionada !== 'Todos') {
      filtrados = productos.filter(p => p.categoria === categoriaSeleccionada);
    }

    aplicarFiltroPrecio(filtrados, rango);
  };

  const aplicarFiltroPrecio = (productosBase, rango) => {
    let resultado = productosBase;

    if (rango === '0-100') {
      resultado = productosBase.filter(p => p.precio <= 100);
    } else if (rango === '100-200') {
      resultado = productosBase.filter(p => p.precio > 100 && p.precio <= 200);
    } else if (rango === '200-300') {
      resultado = productosBase.filter(p => p.precio > 200 && p.precio <= 300);
    }

    setProductosFiltrados(resultado);
  };

  return (
    <section className="tienda">
      <div className="container">
        <h2>{categoriaFija ? `Productos para ${categoriaFija}` : 'Nuestros Productos'}</h2>

        {!categoriaFija && (
          <div className="filtros">
            <button onClick={() => filtrarCategoria('Todos')}>Todos</button>
            <button onClick={() => filtrarCategoria('Hombre')}>Hombre</button>
            <button onClick={() => filtrarCategoria('Mujer')}>Mujer</button>
            <button onClick={() => filtrarCategoria('Unisex')}>Unisex</button>

            <select value={precioSeleccionado} onChange={(e) => filtrarPorPrecio(e.target.value)}>
              <option value="todos">Todos los precios</option>
              <option value="0-100">Q0 - Q100</option>
              <option value="100-200">Q100 - Q200</option>
              <option value="200-300">Q200 - Q300</option>
            </select>
          </div>
        )}

        {categoriaFija && (
          <div className="filtros">
            <select value={precioSeleccionado} onChange={(e) => filtrarPorPrecio(e.target.value)}>
              <option value="todos">Todos los precios</option>
              <option value="0-100">Q0 - Q100</option>
              <option value="100-200">Q100 - Q200</option>
              <option value="200-300">Q200 - Q300</option>
            </select>
          </div>
        )}

        <div className="productos-grid">
          {productosFiltrados.length === 0 ? (
            <p>Cargando productos...</p>
          ) : (
            productosFiltrados.map(producto => (
              <div key={producto.id} className="producto-card">
                <img
                  src={`http://localhost:3001/public/${producto.imagen}`}
                  alt={producto.nombre}
                  onError={(e) => {
                    e.target.src = 'data:image/svg+xml,%3Csvg xmlns=%22http://www.w3.org/2000/svg%22 width=%22200%22 height=%22200%22%3E%3Crect fill=%22%23ecf0f1%22 width=%22200%22 height=%22200%22/%3E%3Ctext x=%2250%25%22 y=%2250%25%22 text-anchor=%22middle%22 dy=%22.3em%22 fill=%22%2395a5a6%22%3EImagen%3C/text%3E%3C/svg%3E';
                  }}
                />
                <span className="categoria">{producto.categoria}</span>
                <h3>{producto.nombre}</h3>
                <p>{producto.descripcion}</p>
                <div className="precio">Q{producto.precio.toFixed(2)}</div>
                <button onClick={() => agregarAlCarrito(producto)}>
                  Agregar al Carrito
                </button>
              </div>
            ))
          )}
        </div>
      </div>
    </section>
  );
}

export default Tienda;
