import React, { useEffect, useState } from 'react';
import { api } from '../../api';
import { auth } from '../../auth';
import { useNavigate } from 'react-router-dom';

// Helpers
const formatoQ = (n) => `Q${Number(n || 0).toFixed(2)}`;
const calcTotalVenta = (venta) => {
  if (typeof venta?.total === 'number') return venta.total;
  const items = Array.isArray(venta?.items) ? venta.items : [];
  return items.reduce((acc, it) => {
    const precio = Number(it?.precio || 0);
    const cantidad = Number(it?.cantidad || 0);
    const sub = typeof it?.subtotal === 'number' ? it.subtotal : precio * cantidad;
    return acc + sub;
  }, 0);
};

function AdminPage() {
  const navigate = useNavigate();
  const [ventas, setVentas] = useState([]);
  const [ventaSeleccionada, setVentaSeleccionada] = useState(null);
  // ... resto de estados y efectos

  // ... cargarVentas que trae /api/ventas o localStorage

  return (
    <section className="admin">
      <div className="container">
        {/* Lista de pedidos */}
        {ventas.length > 0 && (
          <div className="card" style={{ padding: 0 }}>
            {/* encabezados... */}
            {ventas.map((v) => {
              const fecha = v.fecha ? new Date(v.fecha) : new Date(v.id || Date.now());
              const fechaTxt = fecha.toLocaleString();
              const clienteNombre = v.cliente?.nombre || '—';
              const entrega = v.entrega || '—';
              const pago = v.pago || '—';
              const total = calcTotalVenta(v); // <= calcular total

              return (
                <div
                  key={v.id || fecha.getTime()}
                  className="admin-producto-item"
                  style={{ display: 'grid', gridTemplateColumns: '160px 1fr 120px 140px 110px', cursor: 'pointer' }}
                  onClick={() => setVentaSeleccionada(v)}
                >
                  <div>{fechaTxt}</div>
                  <div>{clienteNombre}</div>
                  <div style={{ textTransform: 'capitalize' }}>{entrega}</div>
                  <div style={{ textTransform: 'capitalize' }}>{pago}</div>
                  <div><strong>{formatoQ(total)}</strong></div> {/* <= mostrar total */}
                </div>
              );
            })}
          </div>
        )}

        {/* Detalle del pedido */}
        {ventaSeleccionada && (
          <div className="card" style={{ marginTop: 16 }}>
            {/* ... items, cliente, etc. */}
            <div className="carrito-total" style={{ marginTop: 0 }}>
              <strong>Total</strong>
              <strong>{formatoQ(calcTotalVenta(ventaSeleccionada))}</strong> {/* <= total detalle */}
            </div>
          </div>
        )}
      </div>
    </section>
  );
}

export default AdminPage;
