import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import Tienda from './components/Tienda';
import Carrito from './components/Carrito';
import Admin from './components/Admin';

function App() {
  const [carrito, setCarrito] = useState(() => {
    const carritoGuardado = localStorage.getItem('carrito');
    return carritoGuardado ? JSON.parse(carritoGuardado) : [];
  });

  useEffect(() => {
    localStorage.setItem('carrito', JSON.stringify(carrito));
  }, [carrito]);

  const agregarAlCarrito = (producto) => {
    setCarrito([...carrito, producto]);
    alert('Producto agregado al carrito');
  };

  const eliminarDelCarrito = (index) => {
    const nuevoCarrito = carrito.filter((_, i) => i !== index);
    setCarrito(nuevoCarrito);
  };

  const finalizarCompra = () => {
    if (carrito.length === 0) {
      alert('El carrito est√° vac√≠o');
      return;
    }
    const total = carrito.reduce((sum, p) => sum + p.precio, 0);
    alert(`¬°Compra realizada con √©xito! Total: Q${total.toFixed(2)}`);
    setCarrito([]);
  };

  return (
    <Router>
      <div className="App">
        <header>
          <div className="container">
            <h1>üõçÔ∏è PraxShop</h1>
            <nav>
              <Link to="/">Tienda</Link>
              <Link to="/hombre">Hombre</Link>
              <Link to="/mujer">Mujer</Link>
              <Link to="/carrito">üõí Carrito ({carrito.length})</Link>
              <Link to="/admin">Admin</Link>
            </nav>
          </div>
        </header>

        <main>
          <Routes>
            <Route path="/" element={<Tienda agregarAlCarrito={agregarAlCarrito} />} />
            <Route path="/hombre" element={<Tienda agregarAlCarrito={agregarAlCarrito} categoriaFija="Hombre" />} />
            <Route path="/mujer" element={<Tienda agregarAlCarrito={agregarAlCarrito} categoriaFija="Mujer" />} />
            <Route 
              path="/carrito" 
              element={
                <Carrito
                  carrito={carrito}
                  eliminarDelCarrito={eliminarDelCarrito}
                  finalizarCompra={finalizarCompra}
                />
              } 
            />
            <Route path="/admin" element={<Admin />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
