export const AUTH_KEY = 'admin_token';
export const auth = {
  getToken: () => localStorage.getItem(AUTH_KEY),
  setToken: (t) => localStorage.setItem(AUTH_KEY, t),
  clear: () => localStorage.removeItem(AUTH_KEY),
  isLoggedIn: () => !!localStorage.getItem(AUTH_KEY),
  authHeader: () => {
    const t = localStorage.getItem(AUTH_KEY);
    return t ? { Authorization: `Bearer ${t}` } : {};
  },
};
